import React, { useMemo } from "react";
import MOCK_DATA from "./dummy data/MOCK_DATA.json";
import { useTable, useSortBy, useGlobalFilter, usePagination } from "react-table";

const COLUMNS = [
    {
        Header: 'First Name',
        accessor: 'first_name'
    },
    {
        Header: 'Last Name',
        accessor: 'last_name'
    },
    {
        Header: 'ID',
        accessor: 'id'
    },
    {
        Header: 'Email',
        accessor: 'email'
    }
]

const GlobalFilter = ({ filter, setFilter }) => {
    return (
        <div className="table-filter">
            Search: {' '}
            <input value={filter || ''}
            onChange={e => setFilter(e.target.value)} />
        </div>
    )
}

export default function DataTable() {

    const columns = useMemo(() => COLUMNS, [])
    const data = useMemo(() => MOCK_DATA, [])

    const table = useTable({
        columns,
        data
    },
    useGlobalFilter,
    useSortBy,
    usePagination
    )

    const { getTableProps, getTableBodyProps, page, nextPage, previousPage, canNextPage, canPreviousPage, pageOptions, gotoPage, pageCount, prepareRow, state, setGlobalFilter } = table

    const { globalFilter, pageIndex } = state



    return (
        <>
        <GlobalFilter filter={globalFilter} setFilter={setGlobalFilter} />
        <table {...getTableProps()}>
            <thead>                        
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {
                                headerGroup.headers.map(column => (
                                    <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                                        {column.render('Header')}
                                        <span>
                                            {' '}{column.isSorted ? (column.isSortedDesc ? '˄': '˅'):''}
                                        </span>
                                    </th>
                                ))
                            }
                            <th>
                                1
                            </th>
                        </tr>
                        <tr>
                            <ExpandedHeader />
                        </tr>
            </thead>
            <tbody {...getTableBodyProps()}>
                {
                    page.map(row => {
                        prepareRow(row)
                        return(
                            <tr {...row.getRowProps()}>
                                {
                                    row.cells.map((cell) => {
                                        return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                    })
                                }
                                <td></td>
                            </tr>
                        )
                    })
                }
                <tr>
                    <td>
                    </td>
                </tr>
            </tbody>
            <tfoot>
                <tr>
                    <td colSpan="100%">
                        <span className="pageIndicator">
                            Page{' '}
                                {pageIndex+1} of {pageOptions.length}
                        </span>
                        <span className="pageSwitch">
                            Go to page: {' '}
                            <input type='number' defaultValue={pageIndex + 1} onChange={e => {
                                const pageNumber = e.target.value ? Number(e.target.value) - 1 : 0
                                gotoPage(pageNumber) 
                            }}/>
                        </span>
                        <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>{'<<'}</button>
                        <button className="tableNavLeft" onClick={() => previousPage()} disabled={!canPreviousPage}>Previous</button>
                        <button className="tableNavRight" onClick={() => nextPage()} disabled={!canNextPage}>Next</button>
                        <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>{'>>'}</button>
                    </td>
                </tr>
            </tfoot>
        </table>
        <div>
        </div>
        </>
    )
}
